package EjerciciosJava;

public class CaracteresEspeciales {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		var name = "Darwin";
		
		//caracter \n para saltos de linea
		
		System.out.println("nueva linea: \n" + name);
		
		//tabulador
		
		System.out.println("tabulacion: \t" + name);
		
		//retorceso
		
		System.out.println("Retroceso: \b" + name);
		
		// comillas simple
		
		System.out.println("comilla simple: \'" + name + "\'");
		
		//commilla doble
		
		System.out.println("commilla doble \"" + name + "\"");

	}

}
