package EjerciciosJava;

public class Char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char miCaracter = 'a';
		System.out.println("mi caracter: " + miCaracter);
		
		char varChar = '\u0021';
		System.out.println("El caracter es: " + varChar);
		
		char varCharDecimal = 33;
		System.out.println("el valor decimal es: " + varCharDecimal);
		
		char varCharSimbolo = '!';
		System.out.println("el caracter es: " + varCharSimbolo);

	}

}
