package EjerciciosJava;

public class PrimitivosEnteros {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// tipos primitivos enteros: Byte, short, int long
		
		byte numeroByte = 15;
		System.out.println("valor minimo de byte: " + Byte.MIN_VALUE);
		System.out.println("valor maximo de byte: " + Byte.MAX_VALUE);
		
		short numeroShort = 10;
		System.out.println("numeroShort es: " + numeroShort);
		System.out.println("valor minimo short: " + Short.MIN_VALUE);
		System.out.println("valor maximo short: " + Short.MAX_VALUE);
		
		int numeroInt = 12;
		System.out.println("El numero entero es: " + numeroInt);
		System.out.println("valor minimo int: " + Integer.MIN_VALUE);
		System.out.println("valor maximo int: " + Integer.MAX_VALUE);
		
		long numeroLong = 16;
		System.out.println("El numero long es: " + numeroLong);
		System.out.println("valor minimo es: " + Long.MIN_VALUE);
		System.out.println("valor maximo es: " + Long.MAX_VALUE);
		
		
		// primitivos de tipo flotante, es decir con punto decimal. float y double
		
		float numeroFloat = (float) 10.5;
		System.out.println("el flotante es: " + numeroFloat);
		System.out.println("valor minimo: " + Float.MIN_VALUE);
		System.out.println("valor maximo: " + Float.MAX_VALUE);
		
		double numeroDouble = 25;
		System.out.println("El numero double es: " + numeroDouble);
		System.out.println("valor minimo: " + Double.MIN_VALUE);
		System.out.println("valor maximo: " + Double.MAX_VALUE);
		
		
		

	}

}
